package com.Class14;

public class toCharArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//toCharArray()
		String str="saturday";
		char[] array=str.toCharArray();
		for(int i=0; i<array.length; i++) {
			System.out.println(array[i]);
			

	}
    for(char c:array) {
    	System.out.println(c);
    }
  
}
}